CREATE TABLE [vendor].[VLineItem] (
    [id]                 INT            NOT NULL,
    [ban]                VARCHAR (255)  NULL,
    [bill_date]          VARCHAR (255)  NULL,
    [charge_type]        VARCHAR (255)  NULL,
    [client_data1]       VARCHAR (255)  NULL,
    [client_data2]       VARCHAR (255)  NULL,
    [client_data3]       VARCHAR (255)  NULL,
    [btn]                VARCHAR (255)  NULL,
    [wtn]                VARCHAR (255)  NULL,
    [usoc]               VARCHAR (255)  NULL,
    [usoc_desc]          VARCHAR (8000) NULL,
    [quantity]           VARCHAR (255)  NULL,
    [amount]             VARCHAR (255)  NULL,
    [start_date]         VARCHAR (255)  NULL,
    [end_date]           VARCHAR (255)  NULL,
    [reference]          VARCHAR (255)  NULL,
    [sub_charge_type]    VARCHAR (255)  NULL,
    [ctn]                VARCHAR (255)  NULL,
    [incoming_file_name] VARCHAR (255)  NULL,
    [profile]            VARCHAR (255)  NULL,
    [generated_at]       VARCHAR (255)  NULL,
    [product_id]         VARCHAR (255)  NULL,
    [product_quantity]   VARCHAR (255)  NULL,
    [product_amount]     VARCHAR (255)  NULL,
    [period]             VARCHAR (255)  NULL,
    [phrase_id]          VARCHAR (255)  NULL,
    [phrase_name]        VARCHAR (255)  NULL,
    [phrase_desc]        VARCHAR (255)  NULL,
    [VProductId]         AS             (case when isnumeric([product_id])=(1) then CONVERT([int],[product_id],(0))  end) PERSISTED,
    [GLId]               AS             (case when isnumeric([sub_charge_type])=(1) then [sub_charge_type]  end)
);

